import { Box, Typography, styled, useTheme } from "@mui/material";
import LocationOnIcon from "@mui/icons-material/LocationOn";

const GlassCard = styled(Box)(({ theme }) => ({
  background: theme.palette.mode === "dark"
    ? "rgba(0, 0, 0, 0.3)"
    : "rgba(255, 255, 255, 0.2)",
  borderRadius: 20,
  boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.2)",
  backdropFilter: "blur(10px)",
  WebkitBackdropFilter: "blur(10px)",
  border: "1px solid rgba(255, 255, 255, 0.18)",
  padding: theme.spacing(4, 2, 3, 2),
  margin: theme.spacing(2, 0),
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  maxWidth: 340,
  width: "90vw",
  mx: "auto",
}));

const LocationContainer = ({ location, country }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === "dark";
  const iconColor = isDark ? "#ffab91" : "#ff7043";
  const textPrimary = isDark ? "#fff" : "#222";
  const textSecondary = isDark ? "#bbbbbb" : "#555";

  return (
    <GlassCard>
      <Box display="flex" alignItems="center" mb={1}>
        <LocationOnIcon sx={{ color: iconColor, fontSize: 32, mr: 1 }} />
        <Typography variant="h5" sx={{ fontWeight: 700, color: textPrimary }}>
          {location}
        </Typography>
      </Box>
      <Typography variant="subtitle1" sx={{ color: textSecondary, letterSpacing: 1 }}>
        {country}
      </Typography>
    </GlassCard>
  );
};

export default LocationContainer;
