import "./App.css";
import { useState, useMemo } from "react";
import {
  Box,
  ThemeProvider,
  CssBaseline,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { createTheme } from "@mui/material/styles";

import useFetch from "./components/hooks/useFetch";
import SearchBar from "./components/SearchBar";
import Navbar from "./components/Navbar";
// import WeatherMap from "./components/WeatherMap";
import WeatherCard from "./components/WeatherCard"; // New MDB card

function App() {
  // Detect dark mode from system preference
  const prefersDarkMode = useMediaQuery("(prefers-color-scheme: dark)");
  const [darkMode, setDarkMode] = useState(prefersDarkMode);

  // Set up custom MUI theme
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? "dark" : "light",
        },
      }),
    [darkMode]
  );

  // State for location and fetched weather data
  const [location, setLocation] = useState("");
  const [data, fetchData, loading, error] = useFetch();

  // Handle submit
  const handleSubmit = (e) => {
    e.preventDefault();
    fetchData(location);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Navbar darkMode={darkMode} onToggleTheme={() => setDarkMode((prev) => !prev)} />

      <main>
        {/* Search Input */}
        <Box sx={{ display: "flex", justifyContent: "center", my: 3 }}>
          <SearchBar
            value={location}
            handleChange={(e) => setLocation(e.target.value)}
            handleSubmit={handleSubmit}
          />
        </Box>

        {/* Results */}
        <Box sx={{ padding: 2 }}>
          {loading && <Typography variant="h6">Loading...</Typography>}
          {error && (
            <Typography variant="h6" color="error">
              {error}
            </Typography>
          )}

          {data?.main && !loading && !error && (
            <Box
              sx={{
                display: "flex",
                flexDirection: { xs: "column", md: "row" },
                width: "100vw",
                minHeight: "100vh",
                boxSizing: "border-box",
              }}
            >
              {/* Weather Card Section */}
              <Box
                sx={{
                  flex: 1,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "flex-start",
                  width: "100%",
                }}
              >
                <WeatherCard
                  location={data.name}
                  time={new Date().toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })}
                  temp={Math.round(data.main.temp)}
                  iconUrl={`https://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`}
                  humidity={data.main.humidity}
                  wind={data.wind.speed}
                  pressure={data.main.pressure}
                />
              </Box>

              {/* Weather Map Section */}
              <Box
                sx={{
                  flex: 2,
                  width: "100%",
                  minHeight: { xs: 250, md: "100vh" },
                }}
              >
                <WeatherMap
                  lat={data.coord.lat}
                  lon={data.coord.lon}
                  city={data.name}
                  temp={data.main.temp}
                />
              </Box>
            </Box>
          )}
        </Box>
      </main>
    </ThemeProvider>
  );
}

export default App;
