import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { Box, useTheme } from "@mui/material";
import { useEffect } from "react";

// Tile URLs for dark/light mode
const darkTile = "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";
const lightTile = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";

// Helper to recenter map on coordinate change
function RecenterMap({ lat, lon }) {
  const map = useMap();
  useEffect(() => {
    if (lat && lon) {
      map.setView([lat, lon]);
    }
  }, [lat, lon, map]);
  return null;
}

const WeatherMap = ({ lat, lon, city, temp }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === "dark";
  if (!lat || !lon) return null;

  // Custom HTML marker with temperature
  const tempIcon = L.divIcon({
    className: "weather-temp-marker",
    html: `
      <div style="
        display: flex;
        flex-direction: column;
        align-items: center;
        background: rgba(255,255,255,0.85);
        border-radius: 12px;
        padding: 6px 10px;
        box-shadow: 0 2px 8px rgba(0,0,0,0.15);
        font-weight: bold;
        font-size: 1.1rem;
        color: #1976d2;
        border: 2px solid #1976d2;
      ">
        ${Math.round(temp)}°C
      </div>
    `,
    iconSize: [60, 40],
    iconAnchor: [30, 40],
    popupAnchor: [0, -35],
  });

  return (
    <Box
      sx={{
        width: { xs: "100%", md: 420 },            // 420px wide on desktop, full width on mobile
        height: { xs: 250, md: "80vh" },           // Tall rectangle on desktop, short on mobile
        m: { xs: 2, md: 4 },                       // margin on all sides, responsive
        borderRadius: 4,
        boxShadow: 4,
        border: `2px solid ${theme.palette.primary.main}`,
        background: theme.palette.background.paper,
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
      className={isDark ? "leaflet-dark" : ""}
    >
      <MapContainer
        center={[lat, lon]}
        zoom={10}
        style={{
          width: "100%",
          height: "100%",
        }}
        scrollWheelZoom={false}
      >
        <TileLayer
          url={isDark ? darkTile : lightTile}
          attribution='&copy; OpenStreetMap contributors'
        />
        <RecenterMap lat={lat} lon={lon} />
        <Marker position={[lat, lon]} icon={tempIcon}>
          <Popup>
            <b>{city}</b>
            <br />
            Temp: {temp}°C
          </Popup>
        </Marker>
      </MapContainer>
    </Box>
  );
};

export default WeatherMap;
