import React from "react";
import styles from "./CurrentWeatherCard.module.css";

const CurrentWeatherCard = ({ data }) => {
  if (!data) return null;

  // OpenWeather icon URL
  const iconUrl = data.weather_icon
    ? `https://openweathermap.org/img/wn/${data.weather_icon}@4x.png`
    : "";

  return (
    <div className={styles.card}>
      <div className={styles.header}>
        <h2 className={styles.city}>
          {data.city}, {data.country}
        </h2>
        <span className={styles.description}>
          {data.weather_description}
        </span>
      </div>
      <div className={styles.main}>
        {iconUrl && (
          <img
            src={iconUrl}
            alt={data.weather_description}
            className={styles.icon}
          />
        )}
        <div className={styles.tempBlock}>
          <span className={styles.temp}>
            {Math.round(data.temperature)}째C
          </span>
          <span className={styles.feels}>
            Feels like {Math.round(data.feels_like)}째C
          </span>
          <span className={styles.range}>
            Min {Math.round(data.temp_min)}째 / Max {Math.round(data.temp_max)}째
          </span>
        </div>
      </div>
    </div>
  );
};

export default CurrentWeatherCard;
