import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useTheme, useMediaQuery } from "@mui/material";
import { useEffect } from "react";

// Tile URLs for dark/light mode
const darkTile = "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";
const lightTile = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";

// Helper to recenter map on coordinate change
function RecenterMap({ lat, lon }) {
  const map = useMap();
  useEffect(() => {
    if (lat && lon) {
      map.setView([lat, lon]);
    }
  }, [lat, lon, map]);
  return null;
}

// ...imports remain the same

const WeatherMap = ({ lat, lon, city, temp }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === "dark";
  const isMobile = useMediaQuery('(max-width:600px)');
  if (!lat || !lon) return null;

  const tempIcon = L.divIcon({
    className: "weather-temp-marker",
    html: `
      <div style="
        display: flex;
        flex-direction: column;
        align-items: center;
        background: rgba(255,255,255,0.85);
        border-radius: 12px;
        padding: 6px 10px;
        box-shadow: 0 2px 8px rgba(0,0,0,0.15);
        font-weight: bold;
        font-size: 1.1rem;
        color: #1976d2;
        border: 2px solid #1976d2;
      ">
        ${Math.round(temp)}°C
      </div>
    `,
    iconSize: [60, 40],
    iconAnchor: [30, 40],
    popupAnchor: [0, -35],
  });

  return (
    <div
      className={isDark ? "leaflet-dark" : ""}
  style={{
  width: "100%",
  height: isMobile ? 220 : "60vh", // reduced from 250/80vh to 220/60vh
  margin: isMobile ? "16px 0" : "32px 32px 32px 0", // less space on mobile
  border: "2px solid #1976d2",
  borderRadius: "18px",
  boxShadow: "0 4px 24px rgba(25, 118, 210, 0.08)",
  background: isDark ? "#222" : "#f7faff",
  boxSizing: "border-box",
}}


    >
      <MapContainer
        center={[lat, lon]}
        zoom={10}
        style={{
          width: "100%",
          height: "100%",
          borderRadius: "16px",
          minHeight: 200,
        }}
        scrollWheelZoom={false}
      >
        <TileLayer
          url={isDark ? darkTile : lightTile}
          attribution='&copy; OpenStreetMap contributors'
        />
        <RecenterMap lat={lat} lon={lon} />
        <Marker position={[lat, lon]} icon={tempIcon}>
          <Popup>
            <b>{city}</b>
            <br />
            Temp: {temp}°C
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default WeatherMap;
