import React from "react";

/**
 * WeatherIcon component
 * @param {string} iconCode - OpenWeather icon code (e.g. "01d", "02n")
 * @param {string} alt - Alternative text for the icon
 * @param {number} size - Icon size in pixels (default: 80)
 */
const WeatherIcon = ({ iconCode, alt = "Weather icon", size = 80 }) => {
  if (!iconCode) return null;

  // Official OpenWeather icon URL pattern
  const iconUrl = `https://openweathermap.org/img/wn/${iconCode}@4x.png`;

  return (
    <img
      src={iconUrl}
      alt={alt}
      width={size}
      height={size}
      style={{
        display: "block",
        margin: "0 auto",
        filter: "drop-shadow(0 2px 12px rgba(25, 118, 210, 0.10))",
        background: "transparent",
      }}
      loading="lazy"
    />
  );
};

export default WeatherIcon;
