import { Box, useTheme, useMediaQuery, Typography, Divider, Stack } from "@mui/material";
import { motion } from "framer-motion";
import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import { useEffect, useState } from "react";

const fadeVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: (i = 0) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.3,
      duration: 0.6,
      type: "spring",
      stiffness: 50,
    },
  }),
};

const CurrentWeatherContainer = ({ weather }) => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  // Helper to format time from unix timestamp and timezone offset
  const formatTime = (dt, tzOffset) => {
    if (!dt || !tzOffset) return "--";
    const date = new Date((dt + tzOffset) * 1000);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.6 }}
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: isMobile ? "center" : "flex-start",
        gap: 3,
        my: 4,
        width: isMobile ? "90%" : "50vw",
        maxWidth: 500,
        minWidth: 280,
        ml: isMobile ? 0 : 6,
        px: 4,
        py: 3,
        borderRadius: 4,
        background: "rgba(255, 255, 255, 0.1)",
        backdropFilter: "blur(12px)",
        boxShadow: "0 8px 32px rgba(0, 0, 0, 0.2)",
        border: "1px solid rgba(255, 255, 255, 0.2)",
      }}
    >
      {show && (
        <>
          {/* Location */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={0}
            style={{ width: "100%" }}
          >
            <LocationContainer
              country={weather.sys.country}
              location={weather.name}
            />
          </motion.div>

          {/* Temperature */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={1}
            style={{ width: "100%" }}
          >
            <TempContainer temp={weather.main} variant="subtitle1" />
          </motion.div>

          {/* Weather Condition */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={2}
            style={{ width: "100%" }}
          >
            <WeatherContainer weather={weather.weather[0].main} />
          </motion.div>

          {/* Divider */}
          <Divider sx={{ my: 1, width: "100%" }} />

          {/* Additional Weather Details */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={3}
            style={{ width: "100%" }}
          >
            <Stack direction="row" spacing={3} justifyContent="center" width="100%">
              <Box>
                <Typography variant="caption" color="text.secondary">Humidity</Typography>
                <Typography variant="body1">{weather.main.humidity}%</Typography>
              </Box>
              <Box>
                <Typography variant="caption" color="text.secondary">Pressure</Typography>
                <Typography variant="body1">{weather.main.pressure} hPa</Typography>
              </Box>
              <Box>
                <Typography variant="caption" color="text.secondary">Wind</Typography>
                <Typography variant="body1">
                  {weather.wind.speed} m/s {weather.wind.deg ? `(${weather.wind.deg}°)` : ""}
                </Typography>
              </Box>
            </Stack>
          </motion.div>

          {/* Coordinates and Sunrise/Sunset */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={4}
            style={{ width: "100%" }}
          >
            <Stack direction="row" spacing={3} justifyContent="center" width="100%" mt={2}>
              <Box>
                <Typography variant="caption" color="text.secondary">Lat/Lon</Typography>
                <Typography variant="body1">
                  {weather.coord.lat}, {weather.coord.lon}
                </Typography>
              </Box>
              <Box>
                <Typography variant="caption" color="text.secondary">Sunrise</Typography>
                <Typography variant="body1">
                  {formatTime(weather.sys.sunrise, weather.timezone)}
                </Typography>
              </Box>
              <Box>
                <Typography variant="caption" color="text.secondary">Sunset</Typography>
                <Typography variant="body1">
                  {formatTime(weather.sys.sunset, weather.timezone)}
                </Typography>
              </Box>
            </Stack>
          </motion.div>
        </>
      )}
    </Box>
  );
};

export default CurrentWeatherContainer;
