import { Container, Typography, Box, styled } from "@mui/material";

// Change to a soft grey, white, or any color you like
const StyledBox = styled(Box)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper, // neutral, theme-aware
  borderRadius: "10px",
  boxShadow: theme.shadows[2], // subtle shadow for depth
  padding: theme.spacing(3, 0),
}));

const LocationContainer = ({ location, country }) => {
  return (
    <StyledBox marginBottom={2} className="location-container">
      <Container>
        <Typography align="left" sx={{ color: "text.primary" }}>
          <Typography variant="h2" sx={{ color: "primary.main" }}>{location}</Typography>
          <Typography variant="h4" sx={{ color: "text.secondary" }}>{country}</Typography>
        </Typography>
      </Container>
    </StyledBox>
  );
};

export { StyledBox };

export default LocationContainer;
