import { Container, Paper, Stack, Fade } from "@mui/material";
import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import { useState, useEffect } from "react";

const CurrentWeatherContainer = ({ weather }) => {
  // Animation state
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(false);
    // Fade-in animation when weather changes
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  return (
    <Container maxWidth="sm" sx={{ my: 4 }}>
      <Fade in={show} timeout={800}>
        <Paper
          elevation={6}
          sx={{
            p: 3,
            borderRadius: 3,
            background: (theme) => theme.palette.background.paper,
            boxShadow: 6,
            transition: "box-shadow 0.3s",
            "&:hover": {
              boxShadow: 12,
            },
          }}
        >
          <Stack spacing={3} alignItems="center">
            <LocationContainer
              country={weather.sys.country}
              location={weather.name}
            />
            <TempContainer temp={weather.main} variant="subtitle1" />
            <WeatherContainer weather={weather.weather[0].main} />
          </Stack>
        </Paper>
      </Fade>
    </Container>
  );
};

export default CurrentWeatherContainer;
