import React, { useEffect, useState } from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";
import { motion } from "framer-motion";
import LocationContainer from "./LocationContainer";
// import TempContainer from "./TempContainer";
// import WeatherContainer from "./WeatherContainer";
import HumidityContainer from "./HumidityContainer";
import PressureContainer from "./PressureContainer";
import WindContainer from "./WindContainer";
import LatLonContainer from "./LatLonContainer";
import SunTimeContainer from "./SunTimeContainer";

const formatTime = (timestamp) => {
  const date = new Date(timestamp * 1000);
  return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
};

const fadeVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: (i = 0) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.2,
      duration: 0.5,
      type: "spring",
      stiffness: 50,
    },
  }),
};

const CurrentWeatherContainer = ({ weather }) => {
  const [show, setShow] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  if (!weather) {
    return (
      <Box sx={{ color: theme.palette.text.secondary, textAlign: "center", mt: 4 }}>
        Loading weather data...
      </Box>
    );
  }

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.6 }}
      sx={{
        width: { xs: "100%", md: 500, lg: 600 },
        maxWidth: "100%",
        p: 3,
        borderRadius: 4,
        boxShadow: 4,
        border: (theme) => `2px solid ${theme.palette.primary.main}`,
        background:
          theme.palette.mode === "dark"
            ? "rgba(25, 25, 25, 0.7)"
            : "rgba(255, 255, 255, 0.9)",
        backdropFilter: "blur(12px)",
        display: "flex",
        flexDirection: "column",
        gap: 2,
        m: "auto",
        userSelect: "none",
        "&:hover": {
          boxShadow: 8,
          borderColor: theme.palette.secondary.main,
        },
      }}
    >
      {/* Header */}
      <Box display="flex" justifyContent="space-between" alignItems="center">
        <Typography variant="h5" fontWeight="bold">
          {weather.name}, {weather.sys.country}
        </Typography>
        <Typography variant="h6" color="text.secondary">
          {formatTime(Math.floor(Date.now() / 1000))}
        </Typography>
      </Box>

      {/* Temperature & Weather */}
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexDirection="column"
      >
        <Typography variant="h2" fontWeight="bold" color="primary">
          {Math.round(weather.main.temp)}Â°C
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" textTransform="capitalize">
          {weather.weather[0].description}
        </Typography>
      </Box>

      {/* Weather details */}
      <Box display="flex" justifyContent="space-between" flexWrap="wrap" gap={2}>
        <Box flex="1" minWidth={120}>
          <WindContainer speed={weather.wind.speed} deg={weather.wind.deg} />
        </Box>
        <Box flex="1" minWidth={120}>
          <HumidityContainer humidity={weather.main.humidity} />
        </Box>
        <Box flex="1" minWidth={120}>
          <PressureContainer pressure={weather.main.pressure} />
        </Box>
        <Box flex="1" minWidth={120}>
          <SunTimeContainer
            sunrise={formatTime(weather.sys.sunrise)}
            sunset={formatTime(weather.sys.sunset)}
          />
        </Box>
        <Box flex="1" minWidth={120}>
          <LatLonContainer lat={weather.coord.lat} lon={weather.coord.lon} />
        </Box>
      </Box>
    </Box>
  );
};

export default CurrentWeatherContainer;
