import { Card, CardContent, Typography, Box, Divider, Stack } from "@mui/material";
import WbSunnyIcon from "@mui/icons-material/WbSunny";
import CloudIcon from "@mui/icons-material/Cloud";
import GrainIcon from "@mui/icons-material/Grain";
import AcUnitIcon from "@mui/icons-material/AcUnit";
import ThunderstormIcon from "@mui/icons-material/Thunderstorm";
import OpacityIcon from "@mui/icons-material/Opacity";
import { blue, grey, yellow, lightBlue, deepPurple } from "@mui/material/colors";

// Helper function to select icon and color
function getWeatherIconAndColor(main) {
  switch (main?.toLowerCase()) {
    case "clear":
    case "sunny":
      return { icon: <WbSunnyIcon sx={{ fontSize: 60, color: yellow[700] }} />, color: yellow[50] };
    case "clouds":
      return { icon: <CloudIcon sx={{ fontSize: 60, color: grey[500] }} />, color: grey[100] };
    case "rain":
      return { icon: <OpacityIcon sx={{ fontSize: 60, color: blue[400] }} />, color: blue[50] };
    case "drizzle":
      return { icon: <GrainIcon sx={{ fontSize: 60, color: lightBlue[400] }} />, color: lightBlue[50] };
    case "snow":
      return { icon: <AcUnitIcon sx={{ fontSize: 60, color: deepPurple[300] }} />, color: deepPurple[50] };
    case "thunderstorm":
      return { icon: <ThunderstormIcon sx={{ fontSize: 60, color: deepPurple[800] }} />, color: deepPurple[100] };
    default:
      return { icon: <CloudIcon sx={{ fontSize: 60, color: grey[400] }} />, color: grey[50] };
  }
}

export default function WeatherContainer({ weatherData }) {
  if (!weatherData) return null;

  const { weather, main, name, sys, wind } = weatherData;
  const mainWeather = weather?.[0]?.main || "Clouds";
  const description = weather?.[0]?.description || "Cloudy";
  const { icon, color } = getWeatherIconAndColor(mainWeather);

  return (
    <Card
      variant="outlined"
      sx={{
        background: color,
        borderRadius: 4,
        boxShadow: 3,
        width: "100%",
        maxWidth: 430,
        my: 2,
        alignSelf: "stretch",
        mx: "auto",
        '@media (max-width:600px)': {
          my: 1,
        },
      }}
      aria-label={`Weather card for ${name}`}
    >
      <CardContent
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          py: 3,
          gap: 2,
          '@media (max-width:600px)': {
            py: 2,
          },
        }}
      >
        {/* City and Country */}
        <Typography variant="h5" sx={{ fontWeight: 700, mb: 0.5 }}>
          {name}{sys?.country ? `, ${sys.country}` : ""}
        </Typography>
        <Typography variant="subtitle1" sx={{ color: "text.secondary", mb: 2 }}>
          {description.charAt(0).toUpperCase() + description.slice(1)}
        </Typography>
        {/* Icon and Temperature */}
        <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
          {icon}
          <Typography
            variant="h2"
            sx={{
              fontWeight: 700,
              color: "text.primary",
              textShadow: "0 2px 6px rgba(0,0,0,0.10)",
              letterSpacing: 1,
              ml: 2,
              fontSize: { xs: "2.2rem", sm: "3rem", md: "3.5rem" },
            }}
          >
            {main?.temp != null ? Math.round(main.temp) : "--"}&deg;C
          </Typography>
        </Box>
        <Divider sx={{ my: 2, width: "100%" }} />
        {/* Additional Parameters */}
        <Stack direction="row" spacing={3} justifyContent="center" width="100%">
          <Box>
            <Typography variant="caption" color="text.secondary">Feels Like</Typography>
            <Typography variant="body1">{main?.feels_like != null ? Math.round(main.feels_like) : "--"}&deg;C</Typography>
          </Box>
          <Box>
            <Typography variant="caption" color="text.secondary">Humidity</Typography>
            <Typography variant="body1">{main?.humidity != null ? main.humidity : "--"}%</Typography>
          </Box>
          <Box>
            <Typography variant="caption" color="text.secondary">Wind</Typography>
            <Typography variant="body1">{wind?.speed != null ? wind.speed : "--"} m/s</Typography>
          </Box>
        </Stack>
      </CardContent>
    </Card>
  );
}
