import "./App.css";
import { useState, useMemo } from "react";
import { Box, ThemeProvider, CssBaseline, useMediaQuery, Button, Stack } from "@mui/material";
import { createTheme } from "@mui/material/styles";
import useFetch from "./components/hooks/useFetch";
import SearchBar from "./components/SearchBar";
import CurrentWeatherContainer from "./components/CurrentWeatherContainer";
import Navbar from "./components/Navbar";

function App() {
  // Use system preference as initial mode
  const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');
  const [darkMode, setDarkMode] = useState(prefersDarkMode);

  // Theme object updates when darkMode changes
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? "dark" : "light",
        },
      }),
    [darkMode]
  );

  const [location, setLocation] = useState("");
  const [data, fetchData] = useFetch();
  const [geoError, setGeoError] = useState("");

  // Handle city search
  const handleSubmit = (e) => {
    e.preventDefault();
    setGeoError("");
    fetchData(location);
  };

  // Handle geolocation button click
  const handleUseMyLocation = () => {
    setGeoError("");
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const lat = position.coords.latitude;
          const lon = position.coords.longitude;
          // Pass coordinates as a string, e.g. "lat,lon"
          fetchData(`${lat},${lon}`);
        },
        (error) => {
          setGeoError("Unable to retrieve your location. Please allow location access.");
        }
      );
    } else {
      setGeoError("Geolocation is not supported by this browser.");
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Navbar darkMode={darkMode} onToggleTheme={() => setDarkMode((prev) => !prev)} />
      <main>
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            mt: 2,
          }}
        >
          <Stack direction="row" spacing={2}>
            <SearchBar
              value={location}
              handleChange={(e) => setLocation(e.target.value)}
              handleSubmit={handleSubmit}
            />
            <Button variant="outlined" onClick={handleUseMyLocation}>
              Use My Location
            </Button>
          </Stack>
        </Box>
        {geoError && (
          <Box sx={{ color: "error.main", textAlign: "center", mt: 2 }}>
            {geoError}
          </Box>
        )}
        {data.main && <CurrentWeatherContainer weather={data} />}
      </main>
    </ThemeProvider>
  );
}

export default App;
