import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import { Box, Typography, Avatar } from "@mui/material";

const CurrentWeatherContainer = ({ weather }) => {
  return (
    <Box
      sx={{
        minHeight: "40vh",
        width: { xs: "90vw", sm: 350 },
        background: "linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: 4,
        boxShadow: 3,
        p: 4,
        mx: "auto",
        mt: 4,
        backdropFilter: "blur(8px)",
      }}
    >
      <Avatar
        src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@4x.png`}
        alt={weather.weather[0].main}
        sx={{ width: 96, height: 96, mb: 2, bgcolor: "white" }}
      />
      <Box sx={{ mb: 2, width: "100%" }}>
        <LocationContainer
          country={weather.sys.country}
          location={weather.name}
        />
      </Box>
      <Box sx={{ mb: 2, width: "100%" }}>
        <TempContainer temp={weather.main} variant="subtitle1" />
      </Box>
      <Box sx={{ width: "100%" }}>
        <WeatherContainer weather={weather.weather[0].main} />
      </Box>
    </Box>
  );
};

export default CurrentWeatherContainer;
