import { Card, CardContent, Box, Typography, useTheme } from "@mui/material";
import DeviceThermostatIcon from "@mui/icons-material/DeviceThermostat";
import OpacityIcon from "@mui/icons-material/Opacity";

const TempContainer = ({ variant = "h6", temp }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === "dark";

  return (
    <Card
      elevation={4}
      sx={{
        display: "flex",
        alignItems: "stretch",
        borderRadius: 3,
        background: isDark
          ? "rgba(30,30,40,0.85)"
          : "rgba(255,255,255,0.85)",
        boxShadow: isDark
          ? "0 4px 24px 0 rgba(0,0,0,0.5)"
          : "0 4px 24px 0 rgba(31,38,135,0.09)",
        width: "100%",         // Make card full width of parent
        mx: 0,
        my: 2,
        position: "relative",
        overflow: "hidden",
        // Remove minWidth and maxWidth
      }}
    >
      {/* Accent bar on the left */}
      <Box
        sx={{
          width: 6,
          height: "100%",
          bgcolor: isDark ? "primary.light" : "primary.main",
          borderTopLeftRadius: 12,
          borderBottomLeftRadius: 12,
        }}
      />
      <CardContent sx={{ pl: 2, pr: 1, py: 2, width: "100%" }}>
        <Box display="flex" alignItems="center" mb={1}>
          <DeviceThermostatIcon
            sx={{
              color: isDark ? "warning.light" : "warning.main",
              mr: 1,
              fontSize: 28,
            }}
          />
          <Typography
            variant={variant}
            sx={{ fontWeight: 600, color: "text.primary" }}
          >
            Current Temperature:{" "}
            <Box component="span" fontWeight="bold" color="primary.main">
              {temp.feels_like}Â°C
            </Box>
          </Typography>
        </Box>
        <Box display="flex" alignItems="center">
          <OpacityIcon
            sx={{
              color: isDark ? "info.light" : "info.main",
              mr: 1,
              fontSize: 24,
            }}
          />
          <Typography
            variant={variant}
            sx={{ fontWeight: 600, color: "text.primary" }}
          >
            Humidity:{" "}
            <Box component="span" fontWeight="bold" color="info.main">
              {temp.humidity}%
            </Box>
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default TempContainer;
