import { TextField, Container, Box, Fab } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";

const SearchBar = ({ handleSubmit, handleChange, value }) => {
  // Optionally, you can wrap handleSubmit to prevent default form behavior
  const onFormSubmit = (e) => {
    e.preventDefault();
    handleSubmit();
  };

  return (
    <Box marginBottom={3}>
      <Container
        maxWidth="sm"
        sx={{
          display: "flex",
          alignItems: "center",
          flexWrap: "wrap",
          flexDirection: "column",
        }}
      >
        <form
          onSubmit={onFormSubmit}
          style={{
            width: "100%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <TextField
            id="outlined-basic"
            label="Location"
            variant="outlined"
            onChange={handleChange}
            value={value}
            sx={{
              m: 2,
              minWidth: { xs: "200px", sm: "350px" },
              width: "100%",
            }}
            required
          />
          <Fab
            variant="extended"
            size="medium"
            color="primary"
            aria-label="search"
            type="submit"
            sx={{
              mt: 1,
              px: 3,
              boxShadow: 3,
              textTransform: "none",
            }}
          >
            <SearchIcon sx={{ mr: 1 }} />
            Search
          </Fab>
        </form>
      </Container>
    </Box>
  );
};

export default SearchBar;
