import { Container, Typography, Box, styled } from "@mui/material";
import { Input } from "@material-tailwind/react";

// Font Awesome location icon (make sure Font Awesome is loaded in your project)
function LocationInput() {
  return (
    <div className="w-72">
      <Input
        label="Location"
        icon={<i className="fas fa-map-marker-alt" aria-hidden="true" />}
      />
    </div>
  );
}

const StyledBox = styled(Box)(({ theme }) => ({
  backgroundColor: theme.palette.primary.main,
  borderRadius: "10px",
}));

function LocationContainer({ location, country }) {
  return (
    <StyledBox marginBottom={2} className="location-container">
      <Container>
        <Typography align="left">
          <Typography variant="h2">{location}</Typography>
          <Typography variant="h3">{country}</Typography>
        </Typography>
      </Container>
    </StyledBox>
  );
}

// Only export each identifier once!
export { StyledBox, LocationInput };
export default LocationContainer;
