import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import styles from "./MapContainer.module.css";

// Custom marker icon (optional, for modern look)
const markerIcon = new L.Icon({
  iconUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png",
  iconRetinaUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png",
  iconSize: [32, 48],
  iconAnchor: [16, 48],
  popupAnchor: [0, -40],
});

const WeatherMap = ({ coord, city }) => {
  if (!coord?.lat || !coord?.lon) return null;

  return (
    <div className={styles.mapWrapper}>
      <h2 className={styles.mapTitle}>Location Map</h2>
      <MapContainer
        center={[coord.lat, coord.lon]}
        zoom={10}
        scrollWheelZoom={false}
        className={styles.map}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        <Marker position={[coord.lat, coord.lon]} icon={markerIcon}>
          <Popup>
            {city ? <b>{city}</b> : "Selected Location"}
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default WeatherMap;
