import { Box, Typography, Container } from "@mui/material";
import WbSunnyIcon from "@mui/icons-material/WbSunny";
import CloudIcon from "@mui/icons-material/Cloud";
import GrainIcon from "@mui/icons-material/Grain";
import AcUnitIcon from "@mui/icons-material/AcUnit";
import ThunderstormIcon from "@mui/icons-material/Thunderstorm";
import OpacityIcon from "@mui/icons-material/Opacity";
import { blue, grey, yellow, lightBlue, deepPurple } from "@mui/material/colors";

// Helper function to select icon and color
function getWeatherIconAndColor(weather) {
  switch (weather?.toLowerCase()) {
    case "clear":
    case "sunny":
      return { icon: <WbSunnyIcon sx={{ fontSize: 60, color: yellow[700] }} />, color: yellow[50] };
    case "clouds":
      return { icon: <CloudIcon sx={{ fontSize: 60, color: grey[500] }} />, color: grey[100] };
    case "rain":
      return { icon: <OpacityIcon sx={{ fontSize: 60, color: blue[400] }} />, color: blue[50] };
    case "drizzle":
      return { icon: <GrainIcon sx={{ fontSize: 60, color: lightBlue[400] }} />, color: lightBlue[50] };
    case "snow":
      return { icon: <AcUnitIcon sx={{ fontSize: 60, color: deepPurple[300] }} />, color: deepPurple[50] };
    case "thunderstorm":
      return { icon: <ThunderstormIcon sx={{ fontSize: 60, color: deepPurple[800] }} />, color: deepPurple[100] };
    default:
      return { icon: <CloudIcon sx={{ fontSize: 60, color: grey[400] }} />, color: grey[50] };
  }
}

export default function WeatherContainer({ weather }) {
  const { icon, color } = getWeatherIconAndColor(weather);

  return (
    <Box
      sx={{
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexDirection: "column",
        background: color,
        borderRadius: 3,
        py: 3,
        my: 2,
        boxShadow: 3,
      }}
    >
      <Container sx={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        {icon}
        <Typography
          variant="h4"
          sx={{
            mt: 2,
            fontWeight: 600,
            color: "text.primary",
            textShadow: "0 2px 6px rgba(0,0,0,0.10)",
            letterSpacing: 1,
          }}
        >
          {weather}
        </Typography>
      </Container>
    </Box>
  );
}
