import React from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCol,
  MDBContainer,
  MDBIcon,
  MDBRow,
  MDBTypography,
} from "mdb-react-ui-kit";

const formatTime = (timestamp) => {
  const date = new Date(timestamp * 1000);
  return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
};

const CurrentWeatherContainer = ({ weather }) => {
  if (!weather) {
    return (
      <MDBContainer className="mt-5 text-center text-white">
        Loading weather data...
      </MDBContainer>
    );
  }

  return (
    <section className="vh-100" style={{ backgroundColor: "#4B515D" }}>
      <MDBContainer className="h-100">
        <MDBRow className="justify-content-center align-items-center h-100">
          <MDBCol md="8" lg="6" xl="4">
            <MDBCard style={{ color: "#4B515D", borderRadius: "35px" }}>
              <MDBCardBody className="p-4">
                {/* Header: City and time */}
                <div className="d-flex">
                  <MDBTypography tag="h6" className="flex-grow-1">
                    {weather.name}, {weather.sys.country}
                  </MDBTypography>
                  <MDBTypography tag="h6">
                    {formatTime(Math.floor(Date.now() / 1000))}
                  </MDBTypography>
                </div>

                {/* Temperature and description */}
                <div className="d-flex flex-column text-center mt-5 mb-4">
                  <MDBTypography
                    tag="h6"
                    className="display-4 mb-0 font-weight-bold"
                    style={{ color: "#1C2331" }}
                  >
                    {Math.round(weather.main.temp)}Â°C
                  </MDBTypography>
                  <span className="small" style={{ color: "#868B94", textTransform: "capitalize" }}>
                    {weather.weather[0].description}
                  </span>
                </div>

                {/* Weather details: wind, humidity, sunrise, icon */}
                <div className="d-flex align-items-center justify-content-between">
                  <div style={{ fontSize: "1rem", color: "#868B94" }}>
                    <div>
                      <MDBIcon fas icon="wind fa-fw" />{" "}
                      <span className="ms-1">{weather.wind.speed} km/h</span>
                    </div>
                    <div>
                      <MDBIcon fas icon="tint fa-fw" />{" "}
                      <span className="ms-1">{weather.main.humidity}%</span>
                    </div>
                    <div>
                      <MDBIcon fas icon="sun fa-fw" />{" "}
                      <span className="ms-1">
                        Sunrise: {formatTime(weather.sys.sunrise)}
                      </span>
                    </div>
                  </div>
                  <div>
                    {/* You can replace this with a dynamic icon if you want */}
                    <img
                      src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-weather/ilu1.webp"
                      width="100px"
                      alt="weather icon"
                    />
                  </div>
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
};

export default CurrentWeatherContainer;
