import { Box, Fade, useTheme, useMediaQuery } from "@mui/material";
import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import { useState, useEffect } from "react";

const CurrentWeatherContainer = ({ weather }) => {
  // Animation state
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  // Responsive: left-align on desktop, center on mobile
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: isMobile ? "center" : "flex-start",
        gap: 3,
        my: 4,
        // Responsive width: 100% on mobile, up to 50% (middle) on desktop
        width: isMobile ? "100%" : "50vw",
        maxWidth: 480,
        minWidth: 260,
        ml: isMobile ? 0 : 6, // add left margin on desktop
      }}
    >
      <Fade in={show} timeout={800} unmountOnExit>
        <Box width="100%">
          <LocationContainer
            country={weather.sys.country}
            location={weather.name}
          />
        </Box>
      </Fade>
      <Fade in={show} timeout={1100} unmountOnExit>
        <Box width="100%">
          <TempContainer temp={weather.main} variant="subtitle1" />
        </Box>
      </Fade>
      <Fade in={show} timeout={1400} unmountOnExit>
        <Box width="100%">
          <WeatherContainer weather={weather.weather[0].main} />
        </Box>
      </Fade>
    </Box>
  );
};

export default CurrentWeatherContainer;
