import { Box, useTheme, useMediaQuery, Typography, Grid, Card, CardContent } from "@mui/material";
import { motion } from "framer-motion";
import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import HumidityContainer from './HumidityContainer';
import PressureContainer from './PressureContainer';
import WindContainer from './WindContainer';
import LatLonContainer from './LatLonContainer';
import SunTimeContainer from "./SunTimeContainer";
import { useEffect, useState } from "react";

// Function to format the Unix timestamp into HH:mm time format
const formatTime = (timestamp) => {
  const date = new Date(timestamp * 1000); // Convert Unix timestamp to milliseconds
  return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }); // Format to HH:mm
};

const fadeVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: (i = 0) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.3,
      duration: 0.6,
      type: "spring",
      stiffness: 50,
    },
  }),
};

const CurrentWeatherContainer = ({ weather }) => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.6 }}
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: isMobile ? "center" : "flex-start",
        gap: 3,
        my: 4,
        width: isMobile ? "90%" : "50vw",
        maxWidth: 600,
        minWidth: 280,
        ml: isMobile ? 0 : 6,
        px: 4,
        py: 3,
        borderRadius: 4,
        background: "linear-gradient(135deg, rgba(255, 255, 255, 0.1), rgba(0, 0, 0, 0.2))",
        backdropFilter: "blur(10px)",
        boxShadow: "0 12px 36px rgba(0, 0, 0, 0.3)",
        border: "1px solid rgba(255, 255, 255, 0.3)",
      }}
    >
      {show && (
        <>
          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={0} style={{ width: "100%" }}>
            <LocationContainer country={weather.sys.country} location={weather.name} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={1} style={{ width: "100%" }}>
            <TempContainer temp={weather.main} variant="h4" sx={{ fontWeight: 600, color: "#ffffff" }} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={2} style={{ width: "100%" }}>
            <WeatherContainer weather={weather.weather[0].main} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={3} style={{ width: "100%" }}>
            <HumidityContainer humidity={weather.main.humidity} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={4} style={{ width: "100%" }}>
            <PressureContainer pressure={weather.main.pressure} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={5} style={{ width: "100%" }}>
            <WindContainer speed={weather.wind.speed} deg={weather.wind.deg} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={6} style={{ width: "100%" }}>
            <LatLonContainer lat={weather.coord.lat} lon={weather.coord.lon} />
          </motion.div>

          <motion.div variants={fadeVariant} initial="hidden" animate="visible" custom={7} style={{ width: "100%" }}>
            <SunTimeContainer
              sunrise={formatTime(weather.sys.sunrise)}
              sunset={formatTime(weather.sys.sunset)}
            />
          </motion.div>
        </>
      )}
    </Box>
  );
};

export default CurrentWeatherContainer;
