import "./App.css";
import { useState, useMemo, useEffect, useMediaQuery} from "react";
import { Box, ThemeProvider, CssBaseline, Typography } from "@mui/material";
import { createTheme } from "@mui/material/styles";
import useFetch from "./components/hooks/useFetch";
import SearchBar from "./components/SearchBar";
import CurrentWeatherContainer from "./components/CurrentWeatherContainer";
import Navbar from "./components/Navbar";
import WeatherMap from "./components/WeatherMap";

function App() {
  const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');
  const [darkMode, setDarkMode] = useState(prefersDarkMode);

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? "dark" : "light",
        },
      }),
    [darkMode]
  );

  const [location, setLocation] = useState("");
  const [geoTried, setGeoTried] = useState(false); // Track if geolocation was attempted
  const [data, fetchData, loading, error] = useFetch();

  // Try geolocation on mount
  useEffect(() => {
    if (!geoTried) {
      if ("geolocation" in navigator) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            fetchData({ lat: position.coords.latitude, lon: position.coords.longitude });
            setGeoTried(true);
          },
          () => {
            setGeoTried(true); // User denied or error, allow manual input
          }
        );
      } else {
        setGeoTried(true); // Geolocation not supported
      }
    }
  }, [geoTried, fetchData]);

  // Handler for city search
  const handleSubmit = (e) => {
    e.preventDefault();
    fetchData(location);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Navbar darkMode={darkMode} onToggleTheme={() => setDarkMode((prev) => !prev)} />
      <main>
        <Box sx={{ display: "flex", justifyContent: "center", my: 3 }}>
          {/* Only show search bar if geolocation failed or denied */}
          {geoTried && (
            <SearchBar
              value={location}
              handleChange={(e) => setLocation(e.target.value)}
              handleSubmit={handleSubmit}
            />
          )}
        </Box>
        <Box sx={{ padding: 2 }}>
          {loading && <Typography variant="h6">Loading...</Typography>}
          {error && <Typography variant="h6" color="error">{error}</Typography>}
          {data?.main && !loading && !error && (
            <Box
              sx={{
                display: "flex",
                flexDirection: { xs: "column", md: "row" },
                width: "100vw",
                minHeight: "100vh",
                boxSizing: "border-box",
              }}
            >
              <Box
                sx={{
                  flex: 1,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "flex-start",
                  width: "100%",
                }}
              >
                <CurrentWeatherContainer weather={data} />
              </Box>
              <Box
                sx={{
                  flex: 2,
                  width: "100%",
                  minHeight: { xs: 250, md: "100vh" },
                }}
              >
                <WeatherMap
                  lat={data.coord.lat}
                  lon={data.coord.lon}
                  city={data.name}
                  temp={data.main.temp}
                />
              </Box>
            </Box>
          )}
        </Box>
      </main>
    </ThemeProvider>
  );
}

export default App;
