import { Box, useTheme, useMediaQuery } from "@mui/material";
import { motion } from "framer-motion";
import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import HumidityContainer from './HumidityContainer';
import PressureContainer from './PressureContainer';
import WindContainer from './WindContainer';
import LatLonContainer from './LatLonContainer';
import SunTimeContainer from "./SunTimeContainer";
import { useEffect, useState } from "react";

const formatTime = (timestamp) => {
  const date = new Date(timestamp * 1000);
  return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
};

const fadeVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: (i = 0) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.2,
      duration: 0.5,
      type: "spring",
      stiffness: 50,
    },
  }),
};

const CurrentWeatherContainer = ({ weather }) => {
  const [show, setShow] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.6 }}
      sx={{
        display: "flex",
        flexDirection: isMobile ? "column" : "row",
        flexWrap: "wrap",
        justifyContent: isMobile ? "center" : "flex-start",
        alignItems: "flex-start",
        gap: 2,
        my: 4,
        px: 3,
        py: 3,
        ml: isMobile ? 0 : 6,
        width: isMobile ? "90%" : "90vw",
        maxWidth: "100%",
        borderRadius: 4,
        background: "rgba(255, 255, 255, 0.1)",
        backdropFilter: "blur(12px)",
        boxShadow: "0 8px 32px rgba(0, 0, 0, 0.2)",
        border: "1px solid rgba(255, 255, 255, 0.2)",
      }}
    >
      {show && (
        <>
          {[ 
            <LocationContainer country={weather.sys.country} location={weather.name} />,
            <TempContainer temp={weather.main} variant="subtitle1" />,
            <WeatherContainer weather={weather.weather[0].main} />,
            <HumidityContainer humidity={weather.main.humidity} />,
            <PressureContainer pressure={weather.main.pressure} />,
            <WindContainer speed={weather.wind.speed} deg={weather.wind.deg} />,
            <LatLonContainer lat={weather.coord.lat} lon={weather.coord.lon} />,
            <SunTimeContainer sunrise={formatTime(weather.sys.sunrise)} sunset={formatTime(weather.sys.sunset)} />,
          ].map((Component, index) => (
            <motion.div
              key={index}
              variants={fadeVariant}
              initial="hidden"
              animate="visible"
              custom={index}
              style={{
                width: isMobile ? "100%" : "calc(33% - 1rem)",
                minWidth: 200,
                flex: "1 1 auto",
              }}
            >
              {Component}
            </motion.div>
          ))}
        </>
      )}
    </Box>
  );
};

export default CurrentWeatherContainer;
