import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useTheme } from "@mui/material/styles";
import { useEffect } from "react";

const darkTile =
  "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";
const lightTile =
  "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";

// Helper component to recenter the map when lat/lon change
function RecenterMap({ lat, lon }) {
  const map = useMap();
  useEffect(() => {
    if (lat && lon) {
      map.setView([lat, lon]);
    }
  }, [lat, lon, map]);
  return null;
}

const WeatherMap = ({ lat, lon, city, temp }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === "dark";
  if (!lat || !lon) return null;

  // Custom HTML marker with temperature
  const tempIcon = L.divIcon({
    className: "weather-temp-marker",
    html: `
      <div style="
        display: flex;
        flex-direction: column;
        align-items: center;
        background: rgba(255,255,255,0.85);
        border-radius: 12px;
        padding: 6px 10px;
        box-shadow: 0 2px 8px rgba(0,0,0,0.15);
        font-weight: bold;
        font-size: 1.1rem;
        color: #1976d2;
        border: 2px solid #1976d2;
      ">
        ${Math.round(temp)}°C
      </div>
    `,
    iconSize: [60, 40],
    iconAnchor: [30, 40],
    popupAnchor: [0, -35],
  });

  return (
    <div className={isDark ? "leaflet-dark" : ""} style={{ width: "100%", marginTop: 16 }}>
      <MapContainer
        center={[lat, lon]}
        zoom={10}
        style={{ height: "300px", width: "100%", borderRadius: "12px" }}
        scrollWheelZoom={false}
      >
        <TileLayer
          url={isDark ? darkTile : lightTile}
          attribution='&copy; OpenStreetMap contributors'
        />
        {/* This will recenter the map when lat/lon change */}
        <RecenterMap lat={lat} lon={lon} />
        <Marker position={[lat, lon]} icon={tempIcon}>
          <Popup>
            <b>{city}</b>
            <br />
            Temp: {temp}°C
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default WeatherMap;
