import CardMedia from "@mui/material/CardMedia";

// ...inside your WeatherContainer function/component:

<Card
  variant="outlined"
  sx={{
    background: color,
    borderRadius: 4,
    boxShadow: 3,
    maxWidth: 340,
    mx: "auto",
    my: 2,
    position: "relative", // for overlaying content
    overflow: "hidden",
  }}
  aria-label={`Weather card for ${weather}`}
>
  {/* Optional: animated background */}
  {weather === "rain" && (
    <CardMedia
      component="video"
      src="/rain.mp4"
      autoPlay
      loop
      muted
      sx={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        objectFit: "cover",
        zIndex: 0,
        opacity: 0.6,
      }}
    />
  )}
  {/* Card content stays above */}
  <CardContent
    sx={{
      display: "flex",
      flexDirection: "row",
      alignItems: "center",
      py: 4,
      gap: 2,
      position: "relative",
      zIndex: 1,
    }}
  >
    <Box sx={{ display: "flex", alignItems: "center" }}>
      {icon}
    </Box>
    <Typography
      variant="h4"
      sx={{
        fontWeight: 600,
        color: "text.primary",
        textShadow: "0 2px 6px rgba(0,0,0,0.10)",
        letterSpacing: 1,
        textTransform: "capitalize",
        ml: 2,
      }}
    >
      {weather}
    </Typography>
  </CardContent>
</Card>
