import { Box, Typography, styled } from "@mui/material";
import LocationOnIcon from "@mui/icons-material/LocationOn";

const GlassCard = styled(Box)(({ theme }) => ({
  background: "rgba(255, 255, 255, 0.2)", // glassmorphism effect
  borderRadius: 20,
  boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.2)",
  backdropFilter: "blur(10px)",
  WebkitBackdropFilter: "blur(10px)",
  border: "1px solid rgba(255, 255, 255, 0.18)",
  padding: theme.spacing(4, 2, 3, 2),
  margin: theme.spacing(2, 0),
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  maxWidth: 340,
  width: "90vw",
  mx: "auto",
}));

const LocationContainer = ({ location, country }) => (
  <GlassCard>
    <Box display="flex" alignItems="center" mb={1}>
      <LocationOnIcon sx={{ color: "#ff7043", fontSize: 32, mr: 1 }} />
      <Typography variant="h5" sx={{ fontWeight: 700, color: "#222" }}>
        {location}
      </Typography>
    </Box>
    <Typography variant="subtitle1" sx={{ color: "#555", letterSpacing: 1 }}>
      {country}
    </Typography>
  </GlassCard>
);

export default LocationContainer;
