import React, { useEffect, useState } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBTypography,
  MDBIcon,
} from "mdb-react-ui-kit";

import { Box, useTheme, useMediaQuery } from "@mui/material";
import { motion } from "framer-motion";

import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import HumidityContainer from "./HumidityContainer";
import PressureContainer from "./PressureContainer";
import WindContainer from "./WindContainer";
import LatLonContainer from "./LatLonContainer";
import SunTimeContainer from "./SunTimeContainer";

const formatTime = (timestamp) => {
  const date = new Date(timestamp * 1000);
  return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
};

const fadeVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: (i = 0) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.2,
      duration: 0.5,
      type: "spring",
      stiffness: 50,
    },
  }),
};

export default function CombinedWeatherCard({ weather }) {
  const [show, setShow] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  if (!weather) return null; // or some loading state

  return (
    <section className="vh-100" style={{ backgroundColor: "#4B515D" }}>
      <MDBContainer className="h-100">
        <MDBRow className="justify-content-center align-items-center h-100">
          <MDBCol md="10" lg="8" xl="6">
            <MDBCard
              style={{
                color: "#4B515D",
                borderRadius: "35px",
                overflow: "hidden",
                // Add MUI-like shadows & border:
                boxShadow: theme.shadows[4],
                border: `2px solid ${theme.palette.primary.main}`,
                background:
                  theme.palette.mode === "dark"
                    ? "rgba(25, 25, 25, 0.4)"
                    : "rgba(255, 255, 255, 0.7)",
                backdropFilter: "blur(12px)",
              }}
            >
              <MDBCardBody className="p-4">
                <Box
                  component={motion.div}
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.6 }}
                  sx={{
                    display: "flex",
                    flexDirection: isMobile ? "column" : "row",
                    flexWrap: "wrap",
                    justifyContent: isMobile ? "center" : "space-between",
                    alignItems: "center",
                    gap: 2,
                  }}
                >
                  {/* Location and Time */}
                  <Box sx={{ flexGrow: 1, mb: isMobile ? 2 : 0 }}>
                    <MDBTypography tag="h6" className="mb-2">
                      {weather.name}, {weather.sys.country}
                    </MDBTypography>
                    <MDBTypography tag="h6">
                      {formatTime(Math.floor(Date.now() / 1000))}
                    </MDBTypography>
                  </Box>

                  {/* Temperature and Weather */}
                  <Box
                    sx={{
                      textAlign: "center",
                      flexBasis: 200,
                      flexShrink: 0,
                    }}
                  >
                    <TempContainer
                      temp={weather.main}
                      variant="h3"
                      sx={{ fontWeight: "bold" }}
                    />
                    <WeatherContainer weather={weather.weather[0].main} />
                  </Box>

                  {/* Other Details */}
                  <Box
                    sx={{
                      flexGrow: 1,
                      fontSize: "1rem",
                      display: "flex",
                      flexDirection: "column",
                      gap: 1,
                    }}
                  >
                    <Box>
                      <MDBIcon fas icon="wind fa-fw" style={{ color: "#868B94" }} />{" "}
                      <span className="ms-1">{weather.wind.speed} km/h</span>
                    </Box>
                    <Box>
                      <MDBIcon fas icon="tint fa-fw" style={{ color: "#868B94" }} />{" "}
                      <span className="ms-1">{weather.main.humidity}%</span>
                    </Box>
                    <Box>
                      <MDBIcon fas icon="sun fa-fw" style={{ color: "#868B94" }} />{" "}
                      <span className="ms-1">
                        {(
                          (weather.sys.sunset - weather.sys.sunrise) /
                          3600
                        ).toFixed(1)}{" "}
                        h
                      </span>
                    </Box>
                  </Box>

                  {/* Weather Icon Image */}
                  <Box sx={{ flexShrink: 0 }}>
                    <img
                      src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-weather/ilu1.webp"
                      width="100px"
                      alt="weather icon"
                    />
                  </Box>
                </Box>

                {/* Additional Info Boxes with animation */}
                {show && (
                  <Box
                    sx={{
                      display: "flex",
                      flexWrap: "wrap",
                      justifyContent: isMobile ? "center" : "flex-start",
                      gap: 2,
                      mt: 4,
                    }}
                  >
                    {[
                      <LocationContainer
                        country={weather.sys.country}
                        location={weather.name}
                      />,
                      <TempContainer temp={weather.main} variant="subtitle1" />,
                      <WeatherContainer weather={weather.weather[0].main} />,
                      <HumidityContainer humidity={weather.main.humidity} />,
                      <PressureContainer pressure={weather.main.pressure} />,
                      <WindContainer
                        speed={weather.wind.speed}
                        deg={weather.wind.deg}
                      />,
                      <LatLonContainer lat={weather.coord.lat} lon={weather.coord.lon} />,
                      <SunTimeContainer
                        sunrise={formatTime(weather.sys.sunrise)}
                        sunset={formatTime(weather.sys.sunset)}
                      />,
                    ].map((Component, index) => (
                      <motion.div
                        key={index}
                        variants={fadeVariant}
                        initial="hidden"
                        animate="visible"
                        custom={index}
                        style={{
                          width: 260,
                          height: 180,
                          flexShrink: 0,
                        }}
                      >
                        {Component}
                      </motion.div>
                    ))}
                  </Box>
                )}
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
