import React from "react";

const styles = {
  detailsCard: {
    background: "rgba(255,255,255,0.85)",
    borderRadius: "18px",
    boxShadow: "0 4px 24px rgba(25, 118, 210, 0.09)",
    padding: "1.5rem 2rem",
    minWidth: 240,
    maxWidth: 350,
    margin: "0 auto",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  title: {
    fontSize: "1.3rem",
    fontWeight: 700,
    color: "#1976d2",
    marginBottom: "1.2rem",
    letterSpacing: "1px",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "1.1rem 2.2rem",
    width: "100%",
  },
  detail: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    background: "rgba(240,248,255,0.5)",
    borderRadius: "10px",
    padding: "0.7rem 1rem",
    boxShadow: "0 1px 4px rgba(25, 118, 210, 0.05)",
  },
  label: {
    fontSize: "0.98rem",
    color: "#555",
    marginBottom: "0.3rem",
    fontWeight: 500,
  },
  value: {
    fontSize: "1.1rem",
    color: "#222",
    fontWeight: 700,
  },
  // Responsive styles for mobile
  "@media (maxWidth: 600px)": {
    detailsCard: {
      padding: "1rem 0.5rem",
      maxWidth: "98vw",
    },
    grid: {
      gridTemplateColumns: "1fr",
      gap: "0.7rem 0",
    },
  },
};

const WeatherDetails = ({ data }) => {
  if (!data) return null;

  // Format visibility (meters to km)
  const visibilityKm = data.visibility ? (data.visibility / 1000).toFixed(1) : "N/A";

  return (
    <div style={styles.detailsCard}>
      <h3 style={styles.title}>Weather Details</h3>
      <div
        style={
          window.innerWidth < 600
            ? { ...styles.grid, gridTemplateColumns: "1fr", gap: "0.7rem 0" }
            : styles.grid
        }
      >
        <div style={styles.detail}>
          <span style={styles.label}>Humidity</span>
          <span style={styles.value}>{data.humidity}%</span>
        </div>
        <div style={styles.detail}>
          <span style={styles.label}>Pressure</span>
          <span style={styles.value}>{data.pressure} hPa</span>
        </div>
        <div style={styles.detail}>
          <span style={styles.label}>Wind</span>
          <span style={styles.value}>
            {data.wind_speed} m/s, {data.wind_deg}Â°
          </span>
        </div>
        <div style={styles.detail}>
          <span style={styles.label}>Cloudiness</span>
          <span style={styles.value}>{data.cloudiness}%</span>
        </div>
        <div style={styles.detail}>
          <span style={styles.label}>Visibility</span>
          <span style={styles.value}>{visibilityKm} km</span>
        </div>
        <div style={styles.detail}>
          <span style={styles.label}>Sunrise</span>
          <span style={styles.value}>{data.sunrise}</span>
        </div>
        <div style={styles.detail}>
          <span style={styles.label}>Sunset</span>
          <span style={styles.value}>{data.sunset}</span>
        </div>
      </div>
    </div>
  );
};

export default WeatherDetails;
