import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { Box, useTheme, useMediaQuery } from "@mui/material";
import { useEffect } from "react";

// Tile URLs
const darkTile = "https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png";
const lightTile = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";

// Auto center map on coordinate change
function RecenterMap({ lat, lon }) {
  const map = useMap();
  useEffect(() => {
    if (lat && lon) {
      map.setView([lat, lon]);
    }
  }, [lat, lon, map]);
  return null;
}

const WeatherMap = ({ lat, lon, city, temp }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === "dark";
  const isMobile = useMediaQuery("(max-width:600px)");

  if (!lat || !lon) return null;

  const tempIcon = L.divIcon({
    className: "weather-temp-marker",
    html: `
      <div style="
        display: flex;
        align-items: center;
        justify-content: center;
        background: rgba(255,255,255,0.85);
        border-radius: 8px;
        padding: 4px 8px;
        box-shadow: 0 2px 8px rgba(0,0,0,0.2);
        font-size: 1rem;
        color: #1976d2;
        font-weight: bold;
        border: 2px solid #1976d2;
      ">
        ${Math.round(temp)}°C
      </div>
    `,
    iconSize: [60, 40],
    iconAnchor: [30, 40],
    popupAnchor: [0, -35],
  });

  return (
    <Box
      sx={{
        width: { xs: "100%", md: 900, lg: 1300 },
        height: { xs: 250, md: "80vh" },
        m: { xs: 2, md: 4 },
        p: 3,
        borderRadius: 4,
        boxShadow: 4,
        border: `2px solid ${theme.palette.primary.main}`,
        backgroundColor: theme.palette.background.paper,
        overflow: "hidden",
        transition: "box-shadow 0.3s, border-color 0.3s",
        "&:hover": {
          boxShadow: 8,
          borderColor: theme.palette.secondary.main,
        },
      }}
      className={isDark ? "leaflet-dark" : ""}
    >
      <MapContainer
        center={[lat, lon]}
        zoom={10}
        scrollWheelZoom={false}
        style={{ width: "100%", height: "100%" }}
      >
        <TileLayer
          url={isDark ? darkTile : lightTile}
          attribution='&copy; OpenStreetMap contributors'
        />
        <RecenterMap lat={lat} lon={lon} />
        <Marker position={[lat, lon]} icon={tempIcon}>
          <Popup>
            <b>{city}</b>
            <br />
            Temp: {temp}°C
          </Popup>
        </Marker>
      </MapContainer>
    </Box>
  );
};

export default WeatherMap;
