import { Card, CardContent, Box, useTheme, useMediaQuery, Typography, Divider, Stack } from "@mui/material";
import { motion } from "framer-motion";
import LocationContainer from "./LocationContainer";
import TempContainer from "./TempContainer";
import WeatherContainer from "./WeatherContainer";
import { useEffect, useState } from "react";

const fadeVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: (i = 0) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.3,
      duration: 0.6,
      type: "spring",
      stiffness: 50,
    },
  }),
};

const sectionCardSx = {
  background: "rgba(255,255,255,0.85)",
  borderRadius: 4,
  boxShadow: "0 4px 24px rgba(25, 118, 210, 0.09)",
  mb: 2,
  border: "1px solid rgba(255,255,255,0.18)",
  px: 2,
  py: 1.5,
  width: "100%",
  maxWidth: 480,
};

const CurrentWeatherContainer = ({ weather }) => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(false);
    const timer = setTimeout(() => setShow(true), 100);
    return () => clearTimeout(timer);
  }, [weather]);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  // Helper to format time from unix timestamp and timezone offset
  const formatTime = (dt, tzOffset) => {
    if (!dt || !tzOffset) return "--";
    const date = new Date((dt + tzOffset) * 1000);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.6 }}
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 2,
        my: 4,
        width: isMobile ? "98vw" : "50vw",
        maxWidth: 520,
        minWidth: 280,
        px: isMobile ? 1 : 4,
        py: isMobile ? 1 : 3,
        mx: "auto",
      }}
    >
      {show && (
        <>
          {/* Location Card */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={0}
            style={{ width: "100%" }}
          >
            <Card sx={sectionCardSx} variant="outlined">
              <CardContent sx={{ p: 0 }}>
                <LocationContainer
                  country={weather.sys.country}
                  location={weather.name}
                />
              </CardContent>
            </Card>
          </motion.div>

          {/* Temperature Card */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={1}
            style={{ width: "100%" }}
          >
            <Card sx={sectionCardSx} variant="outlined">
              <CardContent sx={{ p: 0 }}>
                <TempContainer temp={weather.main} variant="h3" />
              </CardContent>
            </Card>
          </motion.div>

          {/* Weather Condition Card */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={2}
            style={{ width: "100%" }}
          >
            <Card sx={sectionCardSx} variant="outlined">
              <CardContent sx={{ p: 0 }}>
                <WeatherContainer weather={weather.weather[0].main} />
              </CardContent>
            </Card>
          </motion.div>

          {/* Details Card */}
          <motion.div
            variants={fadeVariant}
            initial="hidden"
            animate="visible"
            custom={3}
            style={{ width: "100%" }}
          >
            <Card sx={sectionCardSx} variant="outlined">
              <CardContent sx={{ p: 0 }}>
                <Stack direction="row" spacing={3} justifyContent="center" width="100%" mb={1}>
                  <Box>
                    <Typography variant="caption" color="text.secondary">Humidity</Typography>
                    <Typography variant="body1" fontWeight={600}>{weather.main.humidity}%</Typography>
                  </Box>
                  <Box>
                    <Typography variant="caption" color="text.secondary">Pressure</Typography>
                    <Typography variant="body1" fontWeight={600}>{weather.main.pressure} hPa</Typography>
                  </Box>
                  <Box>
                    <Typography variant="caption" color="text.secondary">Wind</Typography>
                    <Typography variant="body1" fontWeight={600}>
                      {weather.wind.speed} m/s {weather.wind.deg ? `(${weather.wind.deg}°)` : ""}
                    </Typography>
                  </Box>
                </Stack>
                <Divider sx={{ my: 1 }} />
                <Stack direction="row" spacing={3} justifyContent="center" width="100%">
                  <Box>
                    <Typography variant="caption" color="text.secondary">Lat/Lon</Typography>
                    <Typography variant="body1" fontWeight={600}>
                      {weather.coord.lat}, {weather.coord.lon}
                    </Typography>
                  </Box>
                  <Box>
                    <Typography variant="caption" color="text.secondary">Sunrise</Typography>
                    <Typography variant="body1" fontWeight={600}>
                      {formatTime(weather.sys.sunrise, weather.timezone)}
                    </Typography>
                  </Box>
                  <Box>
                    <Typography variant="caption" color="text.secondary">Sunset</Typography>
                    <Typography variant="body1" fontWeight={600}>
                      {formatTime(weather.sys.sunset, weather.timezone)}
                    </Typography>
                  </Box>
                </Stack>
              </CardContent>
            </Card>
          </motion.div>
        </>
      )}
    </Box>
  );
};

export default CurrentWeatherContainer;
